import { useEffect, useState } from 'react';
import { useBreakpoints } from './useBreakpoints';
export const useBreakpointsConfig = (config, target, settings) => {
    const { fallback } = config;
    const [value, setValue] = useState(fallback);
    const { matches, breakpoints, size, } = useBreakpoints(target, settings);
    useEffect(() => {
        setValue(config[matches] ?? fallback);
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [matches]);
    return ({
        value,
        matches,
        breakpoints,
        size,
    });
};
